from dataclasses import dataclass@dataclassclass User:    message_id: int = 0class nsql_database:    def __init__(self) -> None:        self.data = {}    # Получение значения по ключу    def get_elem(self, key: int | str) -> User | bool:        if key in self.data:            return self.data[key]        else:            return Falseclass Users(nsql_database):    def __init__(self):        super().__init__()    def __add_user(self, id):        self.data[id] = User()        return self    def __add__(self, id: int):        return self.__add_user(id)    def __iadd__(self, id: int):        return self.__add_user(id)    def __contains__(self, other) -> bool:        if other in self.data:            return True        else:            return False